"use client";
import { cn } from "@/lib/utils";
import TiltedCardWrapper from "../tilted-card-wrapper";
import ElementsSvgOutline from "../elements-svg-outline";

const CharacterAffiliationCard: React.FC<{
  type?: string;
  isActive: boolean;
  onClick: () => void;
}> = ({ onClick, isActive, type = "allies" }) => {
  return (
    <TiltedCardWrapper className="md:w-[250px] w-full aspect-[2/3] rounded-[10px] overflow-hidden transition-none transform-3d backface-hidden">
      <div
        onClick={onClick}
        className={cn(
          "w-full h-full bg-[rgba(143,126,119,.16)] backdrop-blur-lg relative cursor-pointer flex transition-colors text-bronze group",
          type === "allies" && isActive && "bg-green-600/10 text-green-900",
          type === "enemies" && isActive && "bg-red-600/10 text-red-900",
          type === "allies"
            ? "hover:bg-green-600/10 hover:text-green-900"
            : "hover:bg-red-600/10 hover:text-red-900"
        )}
      >
        <ElementsSvgOutline
          className={cn(
            type === "allies" && isActive && "border-green-900",
            type === "enemies" && isActive && "border-red-900",
            type === "allies"
              ? "group-hover:border-green-900"
              : "group-hover:border-red-900"
          )}
        />
        <div className="flex-1 flex flex-col gap-4 items-center justify-center">
          {type === "allies" ? (
            <>
              <SVGIcon1 />
              <p className="font-alegreya font-bold">Allies</p>
            </>
          ) : (
            <>
              <SVGIcon2 />
              <p className="font-alegreya font-bold">Enemies</p>
            </>
          )}
        </div>
      </div>
    </TiltedCardWrapper>
  );
};

export default CharacterAffiliationCard;

